def testpy1(a,b):
	"""
	++++++++++++++++++++++++++++++++++++
	Function to get the sum of x and y. 
	++++++++++++++++++++++++++++++++++++
        Example: 
        >>> import hktest as pu  
        >>> pu.testpy(1,2) 
        -1 
        >>> pu.testpy(3,-2) 
        5 
        >>> pu.testpy(2.4,1.5) 
        0.9 
        >>> pu.testpy('a','b')
        """
	return a - b
